sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
      return;
   }

	// creating GlideAjax record to invoke server side 'Script Include'
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	// specify the method to run
	getEmailAddr.addParam('sysparm_name','getEmail');
	// specify the sys_id for user record searching on the server side
	// taking this from the 'Requested for' field on the form
	getEmailAddr.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	// send a request to server side (GetEmailAddress class, getEmail method (function))
	// response will be processed by the callback function 'populateEmailField'
	getEmailAddr.getXML(populateEmailField);

	// callback function definition - 'response' is a 
	function populateEmailField(response){
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		// replace the current u_requested_for_email value with the one got
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);
	}

}
