sys_script_include.script:
var GetEmailAddress = Class.create();
// extend global AbstractAjaxProcessor (where this is defined?)
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// function to get the email address based on provided sys_id from the user table
	getEmail : function(){
		// create new GlideRecord to store user details retrieved from user table
		var userRecord = new GlideRecord("sys_user");
		// searched user table based on sys_id passed as sysparam_userid
		userRecord.get(this.getParameter('sysparm_userID'));
		// return only email field converted to string
		return userRecord.email+'';
	},

    type: 'GetEmailAddress'
});
