sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	// function to check if date is in the past
	isDatePast : function(strDate){
		// GlideDateTime objects for current and given date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// testing if date is in the past
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}

	},

	// function to check if date is today
	isDateToday : function(strDate){
		// GlideDateTime objects for current and given date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// GlideDate from GlideDateTime above
		var today = rightnow.getLocalDate();
		var testday = testdate.getLocalDate();
		// testing if testday == today
		if (testday.compareTo(today) == 0) {
			return true;
		}
		else {
			return false;
		}

	},

    type: 'NeedItUtils'
};
