sysauto_script.script:
// query NeedItTask table for tasks not Closed Complete but with due date in the next 24 hours
var in24 = new GlideDateTime();
in24.addDaysLocalTime(1);
var rightnow = new GlideDateTime();

var overdueNIT = new GlideRecord('x_58872_needit_needit_task');
overdueNIT.addQuery('due_date','<=',in24);
overdueNIT.addQuery('due_date','>',rightnow);
overdueNIT.addQuery('state','!=',3);
overdueNIT.query();

// message for each overdue task
while (overdueNIT.next()){
	gs.info("Task overdue in 24hrs : "+overdueNIT.number);
	gs.eventQueue('x_58872_needit.dueIn24NeedItTask',overdueNIT,overdueNIT.number,gs.getUserName());
}
