sysauto_script.script:
// change state of all NeedIt tasks overdue longer than set in properties to "Closed Incomplete" (4)

var maxOverdue = gs.getProperty('x_58872_needit.autoCloseOverdue');

var overdueTasks = new GlideRecord('x_58872_needit_needit_task');
overdueTasks.addQuery('due_date','<=',gs.daysAgo(maxOverdue));
overdueTasks.addQuery('state','<',3);
overdueTasks.query();

while (overdueTasks.next()) {
	gs.info('Changing State to "Closed Incomplete" due to long overdue '+overdueTasks.number);
	overdueTasks.state = 4;
	overdueTasks.work_notes = 'Auto closing due to inactivity';
	overdueTasks.update();
}
